import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface LeadFormData {
  fullName: string;
  email: string;
  phone: string;
  budget: string;
  bottlenecks: string;
}

export interface AnalysisResponse {
  strategicInsights: string[];
  recommendedFocus: string;
}

import { GoogleGenAI, Type } from "@google/genai";
import { LeadFormData } from "../types";

export const analyzeBottlenecks = async (data: LeadFormData) => {
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  const prompt = `Analyze the following operational bottlenecks for a fitness business/gym and provide a professional, calm, strategic 3-point insight report.

  Business Context:
  - Name: ${data.fullName}
  - Monthly Budget Range: ${data.budget}
  - Reported Bottlenecks: ${data.bottlenecks}

  Instructions:
  - Do not use hype.
  - Be intelligent and strategic.
  - Suggest how custom AI/automation (not specific software) might solve these.
  - Keep it brief (3 clear points).`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            strategicInsights: {
              type: Type.ARRAY,
              items: { type: Type.STRING },
              description: "Three high-level strategic observations."
            },
            recommendedFocus: {
              type: Type.STRING,
              description: "A single sentence recommending the primary area of infrastructure focus."
            }
          },
          required: ["strategicInsights", "recommendedFocus"]
        }
      }
    });

    return JSON.parse(response.text);
  } catch (error) {
    console.error("Gemini Analysis Error:", error);
    return null;
  }
};

import React, { useEffect, useRef, useState } from 'react';

interface SectionWrapperProps {
  children: React.ReactNode;
  id?: string;
  className?: string;
}

const SectionWrapper: React.FC<SectionWrapperProps> = ({ children, id, className = "" }) => {
  const [isVisible, setIsVisible] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => observer.disconnect();
  }, []);

  return (
    <section 
      id={id} 
      ref={ref}
      className={`transition-all duration-1000 transform ${className} ${
        isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
      }`}
    >
      {children}
    </section>
  );
};

export default SectionWrapper;

